import React from 'react';
import Folders from "./Folders";
import { Link } from "react-router-dom";
import MyContext from "../MyContext"
import PropTypes from "prop-types";
import { ListGroup, ListGroupItem } from 'reactstrap';
import ErrorBoundary from '../ErrorBoundary';

class NotesContainer extends React.Component {
  static contextType = MyContext;
  componentDidUpdate(prevProps) {
    if (prevProps.id !== this.props.id) {
      this.context.setFolderId(this.props.id)
    }
  }
  render() {
    const folderId = this.props.match.params.id || null; //why is this working??? 
    const timestamp = Date.now();
    const { context } = this;
    return (
      <ErrorBoundary>
            <div className="d-flex">
              <div className="folders-pane">
                <Folders />
                <ListGroupItem>
                  <Link to="/folder/add">
                    <button type="button">
                      Add Folder
                         </button>
                  </Link>
                </ListGroupItem>
              </div>
              <div className="notes-pane">
                <ListGroup>
                  {context.notes.map(note =>
                    <ListGroupItem key={note.id}>
                      <div key={note.id}>
                        <Link to={`/note/${note.id}`}>{note.note_name}</Link>
                      </div>
                      <div> {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit' }).format(timestamp)} </div>
                      <button
                        className='Note__delete'
                        type='button'
                        onClick={() => context.deleteNote(note)}
                      > Delete Note </button>
                    </ListGroupItem>
                  )}
                  <ListGroupItem>
                    <Link to="/notes/add">
                      <button type="button">
                        Add Note
                         </button>
                    </Link>
                  </ListGroupItem>
                </ListGroup>
              </div>
            </div>
      </ErrorBoundary>
    )
  }
}

NotesContainer.propTypes = {
  value: PropTypes.string.isRequired
}

export default NotesContainer;
