export default class NoteInfo extends React.Component { 
  componentDidMount(){
  fetch(`http://localhost:8000/notes/1`)
    .then(res => res.json())
    .then(res => {
      this.setState({
          ...this.state, 
          notes: res
      })
  })
  .catch((error => {
      console.error(error);
  }));
}

render () {
console.log ("from noteinfo:", this.state)
return (
  <div> {this.state.notes}</div>
)

}
}


