import React from "react";
import MyContext from "../../MyContext";
import { ListGroup, ListGroupItem } from 'reactstrap';
import { Link } from "react-router-dom";

export default class Folders extends React.Component {
  render() {
    return (
      <ListGroup>
        <MyContext.Consumer>
          {context => {
            console.log(context, /*folder*/)
            return context.folders.map(folder => (
                <ListGroupItem key={folder.name}>
                  <div>
                <Link to={`/folder/${folder.id}`}>{folder.name} </Link>
                </div>
                <button
                  className="Note__delete"
                  type="button"
                  onClick={() => context.deleteFolder(folder)}
                >  Delete Folder</button>  
                 <div> Number of Notes in Folder: {context.notes.filter(note => note.folder_id === folder.id).length} </div>
              </ListGroupItem>
            )
            )  
          }}
        </MyContext.Consumer>
      </ListGroup>

    );
  }
}
