import React, { Component } from 'react';
import PropTypes from 'prop-types';

class NotesByFolder extends Component {
    constructor(props) {
        super(props);
        this.state ={
            notes: [],
        }
    }

    fetchNotesByFolderId = () => {
        const { folder_id } = this.props.match.params;
        fetch(`/notes/${folder_id}`)
          .then((resp) => {
            if (!resp.ok)
              return resp.json().then(e => Promise.reject(e));
            return resp.json();
          })
          .then(data => {
            this.setState({
              notes: data
            })
          })
          .catch(error => {
            console.error({ error });
          });
      }
    
      componentDidUpdate(prevProps) {
        if (this.props.match.params.folder_id !== prevProps.match.params.folder_id) {
          this.fetchNotesByFolderId();
        }
      }
