import React from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { Link } from "react-router-dom";
import NotesContainer from "./components/NotesContainer";
import NoteInfo from "./components/NoteInfo";
import AddFolder from "./components/AddFolder";
import MyContext from "./components/MyContext";
import AddNote from "./components/AddNote/AddNote"

import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      folders: [],
      notes: [],
      folder_id: null
    };
  }

  setFolderId = (folder_id) => {
    this.setState({
      folder_id
    }, () => {
      this.fetchNotesByFolderId()
    });
  };

  componentDidMount() {
   this.fetchFolders();
   this.fetchNotesByFolderId();
  }

  fetchNotesByFolderId = () => {
    const url = this.state.folder_id ? `/folder/${folder_id}` : ``;
    fetch(`http://localhost:8000/notes${url}`)
      .then((resp) => {
        if (!resp.ok)
          return resp.json().then(e => Promise.reject(e));
        return resp.json();
      }).then(data => {
        this.setState({
          notes: data
        })
      })
      .catch(error => {
        console.error({ error });
      });
  }

  fetchFolders = () => {
    fetch(`http://localhost:8000/folders`)
      .then((foldersResponse) => {
        if (!foldersResponse.ok)
          return foldersResponse.json().then(e => Promise.reject(e));
        return foldersResponse.json();
      })
      .then((folders) => {
        this.setState({
          folders
        });
      })
      .catch(error => {
        console.error({ error });
      });
  }

  handleNoteDelete = (note) => {
    const noteId = note.id;
    fetch(`http://localhost:8000/notes/${noteId}`, {
      method: 'DELETE',
      headers: {
        'content-type': 'application/json'
      },
    })
      .then(res => {
        if (res.ok){
          this.fetchFolders()
        }
      })
      .catch(error => {
        console.error({ error })
      })
  };

  //change folder delete to look like note delete

  handleFolderDelete = deletedFolder => {
    const { folders, notes } = this.state;
    fetch(`http://localhost:8000/folders/${deletedFolder.id}`, {
      method: "DELETE",
      headers: {
        "content-type": "application/json"
      }
    })
      .then(res => {
        if (!res.ok) return res.json().then(e => Promise.reject(e));
        return res.json();
      })
      .then(() => {
        const newFolderSet = folders.filter(
          folderObj => !(folderObj.id === deletedFolder.id)
        );
        const newNoteSet = notes.filter(
          noteObj => !(noteObj.folderId === deletedFolder.id)
        );
        this.setState({
          folders: newFolderSet,
          notes: newNoteSet
        });
      })
      .catch(error => {
        console.error({ error });
      });
  };

  addFolder = newFolder => {
    console.log(newFolder);
    const { folders } = this.state;
    folders.push(newFolder);
    this.setState({
      folders
    });
  };

  addNote = newNote => {
    console.log(newNote);
    const { notes } = this.state;
    notes.push(newNote);
    this.setState({
      notes
    });
  };

  render() {
    const { folders, notes } = this.state;
    return (
      <MyContext.Provider
        value={{
          notes,
          folders,
          addFolder: this.addFolder,
          addNote: this.addNote,
          deleteNote: this.handleNoteDelete,
          deleteFolder: this.handleFolderDelete,
          notesById: this.fetchNotesByFolderId
        }}
      >
        <Router>
          <Route path="/" exact component={NotesContainer} />
          <Route path="/folder/:id" exact component={NotesContainer} />
          <Route path="/note/:noteId" exact component={NoteInfo} />
          <Route path="/folder/add" exact component={AddFolder} />
          <Route path="/notes/add" exact component={AddNote} />
        </Router>
      </MyContext.Provider>
    );
  }
}

export default App;
